backdrop-filter:blur(2px);
box-shadow:0 0 1rem 0 rgba(0,0,0,0.2);
cursor:pointer;

display:flex;
flex-direction:column;
z-index:5;

&:hover{
color:${props=>props.theme.body};
background-color:${props=>props.theme.text};
transition:all 0.3s ease;
}

`
