color:inherit;
padding:0.5rem 0;
padding-top:1rem;
font-family:'Karla',sans-serif;
font-weight:700;
border-bottom:1px solid ${props=>props.theme.text}; 

${Box}:hover &{
  border-bottom:1px solid ${props=>props.theme.body}; 
  }

`
